<#

Advent of Code - http://adventofcode.com
2016 - Day 2 - Puzzle 1

Treating the keypad as a 2 dimensional array. The direction helps increment/decrement the row/col indices.


Author: Nasir Zubair

#>

$input = @"





"@ -split "\r\n";



$keypad = @(
            @(1,2,3),
            @(4,5,6),
            @(7,8,9)
            );

$row = 1
$col = 1

$code=@()


$input | ForEach-Object {

    $directions = $_.ToCharArray();

    $directions | ForEach-Object {
        
        switch($_)
        {
            'U' { if( ($row-1) -ge 0) { $row--; } }
            'D' { if( ($row+1) -le 2) { $row++; } }

            'R' { if( ($col+1) -le 2) { $col++; } }
            'L' { if( ($col-1) -ge 0) { $col--; } }
        }

        "{0} -> {1},{2} -> {3}" -f $_,$row,$col,($keypad[$row][$col])

        

    }
    
    $code += ($keypad[$row][$col])

}


"CODE: {0}" -f ($code -join "")
